#!/usr/bin/env python3

import socket

sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)

server_address = '0.0.0.0'
server_port = 65006 #linstin this port

print("Listening on ", server_address, ":", str(server_port), flush=True)
cuspkg = "pkg:0###################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################################Test UDP"


with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s:
    s.bind((server_address, server_port))
    s.listen()
    conn, addr = s.accept()
    with conn:
        print(f"Connected by {addr}")
        while True:
            data = conn.recv(4096)
            if not data:
                break
            print("packet received: ", str(addr), "len: ", len(data), " : ", data)
            #conn.sendall(data)
            conn.sendall(cuspkg.encode('utf-8'))
            print("Sent len: ", str(cuspkg), flush=True)
